const Favorites = () => {

    const favorites = useSelector(state => state.myFavorites)

    return(
        <div>
            {favorites.map(pj => {
                return (
                    <Card
                    id={pj.id}
                    name={pj.name}
                    species={pj.species}
                    gender={pj.gender}
                    image={pj.image}
                  />
                )
            })}
        </div>
    )
}

const mapStateToProps = (state) => {
    return ({
        myFavorites: state.myFavorites
    })
}

export default connect(mapStateToProps,null)(Favorites)
